<script>
    function showTab(tab) {
        const detailContent = document.getElementById('tab-content-detail');
        const teamContent   = document.getElementById('tab-content-team');

        const tabDetail = document.getElementById('tab-detail');
        const tabTeam   = document.getElementById('tab-team');

        if (!detailContent || !teamContent || !tabDetail || !tabTeam) return;

        if (tab === 'detail') {
            detailContent.style.display = 'flex';
            teamContent.style.display   = 'none';

            tabDetail.style.borderBottom = '2px #1E78FF solid';
            tabDetail.querySelector('div').style.color      = '#1E78FF';
            tabDetail.querySelector('div').style.fontWeight = '600';

            tabTeam.style.borderBottom = '1px #E2E8F0 solid';
            tabTeam.querySelector('div').style.color      = '#757575';
            tabTeam.querySelector('div').style.fontWeight = '400';
        }

        if (tab === 'team') {
            detailContent.style.display = 'none';
            teamContent.style.display   = 'flex';

            tabTeam.style.borderBottom = '2px #1E78FF solid';
            tabTeam.querySelector('div').style.color      = '#1E78FF';
            tabTeam.querySelector('div').style.fontWeight = '600';

            tabDetail.style.borderBottom = '1px #E2E8F0 solid';
            tabDetail.querySelector('div').style.color      = '#757575';
            tabDetail.querySelector('div').style.fontWeight = '400';
        }
    }
</script>
